  `kerdes2` varchar(50) NOT NULL,
  `kerdes3` varchar(50) NOT NULL,
  `kerdes4` varchar(50) NOT NULL,
  `kerdes5` varchar(50) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_general_ci;

